mapper.select:
SELECT PI.*,TO_CHAR(PI.potogrf_de,'YYYY-MM-DD') AS grf_de, TO_CHAR(PI.FRST_REGIST_DT, 'YYYY-MM-DD') AS REG_DT, TO_CHAR(PI.LAST_MODF_DT , 'YYYY-MM-DD') AS MODF_DT, CEI.USER_NM FROM TBD_DRONE_PIC_INFO PI LEFT OUTER JOIN COMTNEMPLYRINFO CEI ON PI.EMPLYR_ID = CEI.EMPLYR_ID WHERE 1 = 1 AND PI.EMPLYR_ID = #{emplyrId}
AND PI.SJ LIKE '%' || #{searchWrd} || '%'
AND CEI.USER_NM LIKE '%' || #{searchWrd} || '%'
SJ LAST_MODF_DT DESC
LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

select.if:
SELECT PI.*,TO_CHAR(PI.potogrf_de,'YYYY-MM-DD') AS grf_de, TO_CHAR(PI.FRST_REGIST_DT, 'YYYY-MM-DD') AS REG_DT, TO_CHAR(PI.LAST_MODF_DT , 'YYYY-MM-DD') AS MODF_DT, CEI.USER_NM,
	(SELECT DRONE_PIC_ID FROM TBD_DRONE_PIC_INFO WHERE 1=1 AND SJ IN ( SELECT MAX(SJ) FROM TBD_DRONE_PIC_INFO A WHERE 1=1 AND A.EMPLYR_ID =#{emplyrId} AND  A.SJ < #{sj} 

			AND A.SJ LIKE '%' || #{searchWrd} || '%'


			AND CEI.USER_NM  LIKE '%' || #{searchWrd} || '%'

		)
	) AS PREV_DRON_ID,

	(SELECT SJ FROM TBD_DRONE_PIC_INFO WHERE 1=1 AND SJ IN ( SELECT MAX(SJ) FROM TBD_DRONE_PIC_INFO A WHERE 1=1 AND A.EMPLYR_ID =#{emplyrId} AND   A.SJ < #{sj} 

			AND A.SJ LIKE '%' || #{searchWrd} || '%'


			AND CEI.USER_NM  LIKE '%' || #{searchWrd} || '%'


	)) AS PREV_SJ,

	(SELECT DRONE_PIC_ID FROM TBD_DRONE_PIC_INFO WHERE 1=1 AND SJ IN ( SELECT MIN(SJ) FROM TBD_DRONE_PIC_INFO A WHERE 1=1 AND A.EMPLYR_ID =#{emplyrId} AND   A.SJ > #{sj} 

			AND A.SJ LIKE '%' || #{searchWrd} || '%'


			AND CEI.USER_NM  LIKE '%' || #{searchWrd} || '%'


	 )) AS NEXT_DRON_ID,
	(SELECT SJ FROM TBD_DRONE_PIC_INFO WHERE 1=1 AND SJ IN ( SELECT MIN(SJ) FROM TBD_DRONE_PIC_INFO A WHERE 1=1 AND A.EMPLYR_ID =#{emplyrId} AND   A.SJ > #{sj} 

			AND A.SJ LIKE '%' || #{searchWrd} || '%'


			AND CEI.USER_NM  LIKE '%' || #{searchWrd} || '%'

	 )) AS NEXT_SJ
FROM TBD_DRONE_PIC_INFO PI
	LEFT OUTER JOIN COMTNEMPLYRINFO CEI
		ON PI.EMPLYR_ID = CEI.EMPLYR_ID
WHERE PI.DRONE_PIC_ID = #{dronPicId}

mapper.select:
