mapper.select:
SELECT TGI.GRPHC_ID, /* 그래픽 아이디 */ TGI.SJ, /* 제목 */ CEI.USER_NM AS REGISTER, /* 작성자 */ TO_CHAR(TGI.LAST_MODF_DT, 'YYYY-MM-DD') AS LAST_MODF_DT, /* 갱신 */ PNRS_AT /* 공유 */
SJ TGI.LAST_MODF_DT DESC
LIMIT #{pageUnit} OFFSET (#{pageIndex} - 1) * #{pageUnit}

SELECT GRPHC_ID, CL_ID, SJ, GEOJSON, PNRS_AT, DELETE_AT, REGISTER_ID, FRST_REGIST_DT, LAST_MODF_DT
FROM TBD_GRPHC_INFO
WHERE GRPHC_ID = #{grphcId}

mapper.delete:
UPDATE TBD_GRPHC_INFO
SET DELETE_AT = 'Y'
WHERE GRPHC_ID = #{grphcId}

mapper.select:
SELECT MKR_ID, MKR_NM, IMG, REGISTER_ID, FRST_REGIST_DT
FROM TBD_IMAGE_MKR
WHERE REGISTER_ID = #{registerId}
