mapper.select:
SELECT MI.*, TO_CHAR(MI.FRST_REGIST_DT, 'YYYY-MM-DD') AS REG_DT, TO_CHAR(MI.LAST_MODF_DT , 'YYYY-MM-DD') AS MODF_DT, CEI.USER_NM, st_astext(st_transform(geom, 'EPSG:5179')) as wkt, ${pageIndex} AS PAGE_INDEX, ${pageUnit} AS PAGE_UNIT, ${pageSize} AS PAGE_SIZE FROM TBD_USRT_MEMO_INFO MI LEFT OUTER JOIN COMTNEMPLYRINFO CEI ON MI.EMPLYR_ID = CEI.EMPLYR_ID WHERE 1 = 1 AND MI.EMPLYR_ID = #{emplyrId}
AND MI.SJ LIKE '%' || #{searchWrd} || '%'
AND CEI.USER_NM LIKE '%' || #{searchWrd} || '%'
SJ LAST_MODF_DT DESC
LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

select.if:

SELECT MI.*,st_astext(st_transform(geom, 'EPSG:5179')) as wkt, TO_CHAR(MI.FRST_REGIST_DT, 'YYYY-MM-DD') AS REG_DT, TO_CHAR(MI.LAST_MODF_DT , 'YYYY-MM-DD') AS MODF_DT, CEI.USER_NM,
	(SELECT MEMO_ID FROM TBD_USRT_MEMO_INFO WHERE 1=1 AND SJ IN ( SELECT MAX(SJ) FROM TBD_USRT_MEMO_INFO A WHERE 1=1 AND A.EMPLYR_ID =#{emplyrId} AND  A.SJ < #{sj} 	

			AND A.SJ LIKE '%' || #{searchWrd} || '%'


			AND CEI.USER_NM  LIKE '%' || #{searchWrd} || '%'

		)
	) AS PREV_MEMO_ID,

	(SELECT SJ FROM TBD_USRT_MEMO_INFO WHERE 1=1 AND SJ IN ( SELECT MAX(SJ) FROM TBD_USRT_MEMO_INFO A WHERE 1=1 AND A.EMPLYR_ID =#{emplyrId} AND   A.SJ < #{sj} 

			AND A.SJ LIKE '%' || #{searchWrd} || '%'


			AND CEI.USER_NM  LIKE '%' || #{searchWrd} || '%'


	)) AS PREV_SJ,

	(SELECT MEMO_ID FROM TBD_USRT_MEMO_INFO WHERE 1=1 AND SJ IN ( SELECT MIN(SJ) FROM TBD_USRT_MEMO_INFO A WHERE 1=1 AND A.EMPLYR_ID =#{emplyrId} AND   A.SJ > #{sj} 

			AND A.SJ LIKE '%' || #{searchWrd} || '%'


			AND CEI.USER_NM  LIKE '%' || #{searchWrd} || '%'


	 )) AS NEXT_MEMO_ID,
	(SELECT SJ FROM TBD_USRT_MEMO_INFO WHERE 1=1 AND SJ IN ( SELECT MIN(SJ) FROM TBD_USRT_MEMO_INFO A WHERE 1=1 AND A.EMPLYR_ID =#{emplyrId} AND   A.SJ > #{sj} 

			AND A.SJ LIKE '%' || #{searchWrd} || '%'


			AND CEI.USER_NM  LIKE '%' || #{searchWrd} || '%'

	 )) AS NEXT_SJ
FROM TBD_USRT_MEMO_INFO MI
	LEFT OUTER JOIN COMTNEMPLYRINFO CEI	
		ON MI.EMPLYR_ID = CEI.EMPLYR_ID  
WHERE MI.MEMO_ID = #{memoId}

