mapper.select:
select sn, prprty_no, saeol_cert_yn, input_sys, prprty_nm, posesn_se_cd, prprty_prpos_cd, administ_prprty_cd, account_se_cd, prprty_mng_inscd, chrg_dept_nm, dv_task_mng_inscd, mndt_mng_inscd, prprty_mng_dsgn_ymd, zip, dong_cd, stdg_cd, locplc, mtn_cd, lnbr, ho, tong, ban, spcadrs, spc_dong, spc_ho, rn_addr, prprty_pc, accnut_stdr_amt, acqs_dept_cd, acqs_amt, acqs_ymd, acqs_mth_se_cd, acqs_resn, rgist_yn, recp_no, loan_posbl_yn, sale_lmtt_yn, sale_lmtt_ymd, rm, lad_lndcgr_cd, rl_lndcgr_cd, ar, rl_ar, oalp, acqs_ar, pblonsip_qota1, pblonsip_qota2, spfc, ctypln_dstrc, plan_fclty, dwk, plan_biz, year from tbd_administ_prprty where 1 = 1
and year = #{year}
order by sn asc limit 10 offset #{pageNo}

mapper.delete:
delete from tbd_administ_prprty
 where 1 = 1

   and year = #{year}
