mapper.select:
select pm.publnd_no , pm.ctrt_ymd , pm.cntrctpd , pm.locplc , c.code_nm ldcg_cd , pm.ar , pm.loan_ar , pm.loan_prpos , pm.rrno , pm.loanmn_sndng_yn , pm.nm , pm.addr , pm.zip , pm.cttpc , pm.rm , pm.nht_sndng , pm.atch_papers , pm.cnfirm_matter , pm.edit_yn , pm.year from ( select publnd_no , ctrt_ymd , cntrctpd , locplc , ldcg_cd , ar , loan_ar , loan_prpos , rrno , loanmn_sndng_yn , nm , addr , zip , cttpc , rm , nht_sndng , atch_papers , cnfirm_matter , edit_yn , year from tbd_publnd_mng where del_yn = 'N'
and year = #{yearOption}
) pm inner join comtccmmndetailcode c on (c.code_id = 'J0200' and c.code = pm.ldcg_cd) order by pm.publnd_no limit 10 offset #{pageNo}

