mapper.select:
select count(*)
  from tbd_pbprt_accdt
 where publnd_no = #{publndNo}

mapper.insert:
insert 
  into tbd_pbprt_accdt
	(
		publnd_no
	  , publnd_se
	  , oalp
	  , possesn_ar
	  , possesn_cn
	  , loan_posbl_yn
	  , bsrp_cn
	  , exmnr
	  , atch_file_id
	  , satlit_photo_sn
	  , spt_photo_sn
	)
	values 
	(
		#{publndNo}
	  , #{publndSe}
	  , #{oalp}
	  , #{possesnAr}
	  , #{possesnCn}
	  , #{loanPosblYn}
	  , #{bsrpCn}
	  , #{exmnr}
	  , #{atchFileId}
	  , #{satlitPhotoSn}
	  , #{sptPhotoSn}
	)

mapper.update:
update tbd_pbprt_accdt
   set publnd_se		= #{publndSe}
	 , oalp				= #{oalp}
	 , possesn_ar		= #{possesnAr}
	 , possesn_cn		= #{possesnCn}
	 , loan_posbl_yn	= #{loanPosblYn}
	 , bsrp_cn			= #{bsrpCn}
	 , exmnr			= #{exmnr}
	 , atch_file_id		= #{atchFileId}

	 , satlit_photo_sn 	= null


	 , spt_photo_sn		= null


	 , satlit_photo_sn 	= null
	 , spt_photo_sn		= null


	 , satlit_photo_sn 	= #{satlitPhotoSn}
	 , spt_photo_sn		= #{sptPhotoSn}

 where publnd_no = #{publndNo}

mapper.select:
select spt_photo_sn
  from tbd_pbprt_accdt
 where publnd_no = #{publndNo}

select satlit_photo_sn
  from tbd_pbprt_accdt
 where publnd_no = #{publndNo}

select atch_file_id
  from tbd_pbprt_accdt
 where publnd_no = #{publndNo}
