mapper.select:
select distinct (substring(tlpi.stdr_ym, 1, 4) || '년 ' || substring(tlpi.stdr_ym, 5, 2) || '월') as stdr_ym from ( select li_cd , stdr_ym from tgd_li_popltn_info where 1 = 1
and li_cd like #{liCd} || '%'
) tlpi inner join comtccmmndetailcode c on (c.code = tlpi.li_cd and c.code_id = 'YPLI') order by stdr_ym desc

select c2.code_nm , sum(tlpi.all_popltn_cnt) all_popltn_cnt , sum(tlpi.male_popltn_cnt) male_popltn_cnt , sum(tlpi.female_popltn_cnt) female_popltn_cnt from ( select li_cd , stdr_ym , all_popltn_cnt , male_popltn_cnt , female_popltn_cnt from tgd_li_popltn_info where 1 = 1
and stdr_ym = #{stdrYm}
and stdr_ym = (select max(stdr_ym) from tgd_li_popltn_info)
) tlpi inner join comtccmmndetailcode c2 on (tlpi.li_cd like (substring(c2.code, 1, 8) || '%') and c2.code_id = 'YPE001') group by c2.code_nm, tlpi.li_cd having length(li_cd) = '8'

select distinct (substring(tgpi.stdr_ym, 1, 4) || '년 ' || substring(tgpi.stdr_ym, 5, 2) || '월') as stdr_ym from ( select grid_id , li_cd , stdr_ym from tgd_grid_popltn_info where 1 = 1
and li_cd like #{liCd} || '%'
) tgpi inner join comtccmmndetailcode c on (c.code = tgpi.li_cd and c.code_id = 'YPLI') order by stdr_ym desc

select st_astext(st_centroid(st_union(geom))) as geom from tgd_grid_popltn_info where 1=1 and stdr_ym = #{stdrYm}
and li_cd like '4183025%'
and li_cd like concat(substring(#{liCd},1,8),'%')
